INSERT INTO `locales_source` VALUES ('685','','default','Order of language detection methods for content. If a version of content is available in the detected language, it will be displayed.','','7.23'),('686','','default','Multilingual support','','7.23'),('687','','default','Enable multilingual support for this content type. If enabled, a language selection field will be added to the editing form, allowing you to select from one of the <a href="!languages">enabled languages</a>. If disabled, new posts are saved with the default language. Existing content will not be affected by changing this option.','','7.23'),('688','','default','Site default language','','7.23'),('689','','default','Language','','7.23'),('690','','default','Node type','','7.23'),('691','','default','Text length','','7.23'),('692','','default','Drupal path','','7.23'),('693','','default','File path','','7.23'),('694','','default','Day of week','','7.23'),('695','','default','Theme','','7.23'),('696','','default','Country','','7.23'),('697','','default','Time zone','','7.23'),('698','','default','Date type','','7.23'),('699','','default','Date format','','7.23'),('700','/admin/modules/list/confirm','default','Time interval','','7.23'),('701','','default','Vocabulary','','7.23'),('702','/admin/modules/list/confirm','default','User mail text','','7.23'),('703','','default','Array','','7.23'),('704','','default','Properties','','7.23'),('705','','default','Multiple','','7.23'),('706','','default','E-mail address','','7.23'),('707','/admin/modules/list/confirm','default','Mail text','','7.23'),('708','','default','Subject','','7.23'),('709','','default','Body','','7.23'),('710','','default','Number','','7.23'),('711','','default','Options','','7.23'),('712','','default','Select','','7.23'),('713','','default','String','','7.23'),('714','','default','Unknown','','7.23');
