INSERT INTO `locales_source` VALUES ('3483','','default','Drupal can make a block with the most recent news items of a feed. You can <a href="@block-admin">configure blocks</a> to be displayed in the sidebar of your page. This setting lets you configure the number of news items to show in a feed''s block. If you choose ''0'' these feeds'' blocks will be disabled.','','none'),('3484','','default','You must <em>either</em> upload a file or enter a URL.','','none'),('3485','','default','This URL is not valid.','','none'),('3486','','default','No new feed has been added.','','none'),('3487','','default','The URL %url is invalid.','','none'),('3488','','default','A feed named %title already exists.','','none'),('3489','','default','A feed with the URL %url already exists.','','none'),('3490','','default','Fetchers download data from an external source. Choose a fetcher suitable for the external source you would like to download from.','','none'),('3491','','default','Parsers transform downloaded data into standard structures. Choose a parser suitable for the type of feeds you would like to aggregate.','','none'),('3492','','default','Processors act on parsed feed data, for example they store feed items. Choose the processors suitable for your task.','','none'),('3493','','default','For most aggregation tasks, the default settings are fine.','','none'),('3494','','default','A category named %category already exists. Enter a unique title.','','none'),('3495','','default','Default fetcher','','none'),('3496','','default','Downloads data from a URL using Drupal''s HTTP request handler.','','none'),('3497','','default','Default parser','','none'),('3498','','default','Parses RSS, Atom and RDF feeds.','','none'),('3499','','default','Default processor','','none'),('3500','','default','Creates lightweight records from feed items.','','none'),('3501','','default','Default processor settings','','none'),('3502','','default','Number of items shown in listing pages','','none'),('35