INSERT INTO `locales_source` VALUES ('3956','','default','The content access permissions need to be rebuilt. <a href="@node_access_rebuild">Rebuild permissions</a>.','','none'),('3957','','default','Individual content types can have different fields, behaviors, and permissions assigned to them.','','7.23'),('3958','','default','Content items can be displayed using different view modes: Teaser, Full content, Print, RSS, etc. <em>Teaser</em> is a short format that is typically used in lists of multiple content items. <em>Full content</em> is typically used when the content is displayed on its own page.','','7.23'),('3959','','default','Here, you can define which fields are shown and hidden when %type content is displayed in each view mode, and define how the fields are displayed in each view mode.','','7.23'),('3960','','default','Revisions allow you to track differences between multiple versions of your content, and revert back to older versions.','','none'),('3961','','default','The content on this page has either been modified by another user, or you have already submitted modifications using this form. As a result, your changes cannot be saved.','','none'),('3962','','default','Administer content types','','7.23'),('3963','','default','Warning: Give to trusted roles only; this permission has security implications.','','none'),('3964','','default','View published content','','7.23'),('3965','','default','Access the content overview page','','7.23'),('3966','','default','Bypass content access control','','7.23'),('3967','','default','View content revisions','','7.23'),('3968','','default','Revert content revisions','','7.23'),('3969','','default','Delete content revisions','','7.23'),('3970','','default','View own unpublished content','','7.23'),('3971','','default','Content is sticky at top of lists','','none'),('3972','','default','Content is promoted to the front page','','none'),('3973','','default','Number of recent content items to display','','none'),('3974',