INSERT INTO `locales_source` VALUES ('4248','','default','Inserted into {role_permission} the permissions for role ID !id','','none'),('4249','','default','Aggressive caching was disabled and replaced with normal caching. Read the page caching section in default.settings.php for more information on how to enable similar functionality.','','none'),('4250','','default','%profile_name (%profile-%version)','','none'),('4251','','default','The phpinfo() function has been disabled for security reasons. To see your server''s phpinfo() information, change your PHP settings or contact your server administrator. For more information, <a href="@phpinfo">Enabling and disabling phpinfo()</a> handbook page.','','none'),('4252','','default','An automated attempt to create this directory failed, possibly due to a permissions problem. To proceed with the installation, either create the directory and modify its permissions manually or ensure that the installer has the permissions to create it automatically. For more information, see INSTALL.txt or the <a href="@handbook_url">online handbook</a>.','','none'),('4253','','default','Your configuration has been saved.','','7.23'),('4254','','default','No vocabularies available. <a href="@link">Add vocabulary</a>.','','7.23'),('4255','','default','No terms available. <a href="@link">Add term</a>.','','7.23'),('4256','','default','There is currently no content classified with this term.','','none'),('4257','','default','Tokens related to taxonomy terms.','','7.23'),('4258','','default','Tokens related to taxonomy vocabularies.','','7.23'),('4259','','default','Taxonomy is for categorizing content. Terms are grouped into vocabularies. For example, a vocabulary called "Fruit" would contain the terms "Apple" and "Banana".','','7.23'),('4260','','default','You can reorganize the terms in %capital_name using their drag-and-drop handles, and group terms under a parent term by sliding them under and to the right of the parent.','','7.23'),('4261',