INSERT INTO `locales_target` VALUES ('7301','Неизвестная группа','ru','0','0','0'),('7302','Неизвестный язык','ru','0','0','0'),('7303','Текущая установленная версия','ru','0','0','0'),('7304','Проверять также наличие новых комментариев','ru','0','0','0'),('7305','ID материалов, разделенные '','' или ''+''','ru','0','0','0'),('7306','Не чередовать','ru','0','0','0'),('7307','Альтернативная сортировка','ru','0','0','0'),('7308','Альтернативный порядок сортировки','ru','0','0','0'),('7309','Пустой ввод','ru','0','0','0'),('7310','Не показывать ничего','ru','0','0','0'),('7311','Показывать число','ru','0','0','0'),('7312','Ссылка на загрузку этого файла','ru','0','0','0'),('7313','Установить хлебные крошки для родительского термина','ru','0','0','0'),('7314','При выборе хлебных крошек будет включать в себя все родительские термины, каждое со ссылками на это представление. Заметим, что это работает только, если хотя бы один термин был получен.','ru','0','0','0'),('7315','Глубина будет соответствовать терминам материалов в иерархии. Например, если у вас есть термин "фрукт", и дочерний термин "яблоко", глубиной 1 (или выше), то фильтрация по термину "фрукт" получит материалы, которые помечены как "яблоко", а также " фрукты ". Если значение отрицательное, то наоборот; поиск по слову "яблоко" будет также подбирать материалы помеченые как "фрукт", если глубина -1 (или ниже).','ru','0','0','0');
INSERT INTO `locales_target` VALUES ('7316','Нет словаря','ru','0','0','0'),('7317','Сделать это поле ссылкой на страницу его термина таксономии','ru','0','0','0'),('7318','Сделать это поле ссылкой на страницу его термина','ru','0','0','0'),('7319','Ограничить термины словарем','ru','0','0','0'),('7320','Выберите словарь, из которого будете выбирать термины.','ru','0','0','0'),('7321','Выпадающий','ru','0','0','0'),('7322','Показывать иерархию в выпадающем списке','ru','0','0','0'),('7323','Выбран неверный словарь. Измените его в настройках.','ru','0','0','0'),('7324','Выберите термины из словаря @voc','ru','0','0','0'),('7325','Выберите термины','ru','0','0','0'),('7326','Невозможно найти термин: @terms','ru','0','0','0'),('7327','Невозможно найти термины: @terms','ru','7326','1','0'),('7328','Если вы хотите проверить конкретные словари - отметьте их; если ничего не отмечено - будут пройдены все термины.','ru','0','0','0'),('7329','ID терминов, разделеные '','' или ''+''','ru','0','0','0'),('7330','Текущий язык','ru','0','0','0'),('7331','Опции перевода','ru','0','0','0'),('7332','Опции перевода дают вам право выбирать какой перевод или переводы в наборе включены. Выберите "Текущий язык" или "Язык по умолчанию", чтобы добавить перевод в текущий или стандартный язык соответсвенно. Выберите определенный язык, чтобы добавить перевод в этот язык. Если вы выберите "Все", каждый перевод будет создан в новой строке, что может вызвать появление дубликатов.','ru','0','0','0');
